class Solution(object):
    def minMoves(self, nums):
        """
        :type nums: List[int]
        :rtype: int
        """
        nums.sort()
        count = 0
        a = 0  #number of current max
        length = len(nums)
        max_now = nums[length - 1]
        for i in range(length - 1, -1, -1):
            if nums[i] == max_now:
                a += 1
            else:
                c = nums[i + 1] - nums[i] #difference between current max and second max
                count += c * a
                max_now = nums[i]
                a += 1

        return count


